import streamlit as st
from streamlit_option_menu import option_menu

# Navigasi Sidebar
with st.sidebar:
  selected = option_menu(
    "Hitung Luas Bangun Datar",
    ["Luas Persegi Panjang","Luas Persegi","Luas Segitiga","Luas Lingkaran","Luas Jajar Genjang","Luas Trapesium"],
    icons=["rectangle","square","triangle","circle","square"],
    menu_icon="list",
    default_index=0
  )



# Halaman Hitung Luas Persegi Panjang
if (selected == "Luas Persegi Panjang"):
  st.title("Hitung Luas Persegi Panjang")

  st.image("data:image/png;base64,")

  panjang = st.number_input("Masukkan Panjang :",0)
  lebar = st.number_input("Masukkan Lebar :", 0)
  hitung = st.button("Hitung Luas")

  if hitung:
    luas = panjang * lebar
    st.write("Luas Persegi Panjang adalah : ", luas)
    # st.success(f"Luas Persegi Panjang adalah : {luas}")

# Halaman Hitung Luas Persegi
if (selected == "Luas Persegi"):
  st.title("Hitung Luas Persegi")

  st.image("https://tse3.mm.bing.net/th?id=OIP.W4lctMA5sb0U23jXNkAYQwHaEK&pid=Api&P=0&w=400&h=400")

  sisi = st.number_input("Masukkan Sisi :",0)
  hitung = st.button("Hitung Luas")

  if hitung:
    luas = sisi * sisi
    st.write("Luas Persegi adalah : ", luas)
    # st.success(f"Luas Persegi adalah : {luas}")

# Halaman Hitung Luas Segitiga
if (selected == "Luas Segitiga"):
  st.title("Hitung Luas Segitiga")

  st.image("data:image/jpeg;base64,")

  alas = st.number_input("Masukkan Alas :",0)
  tinggi = st.number_input("Masukkan Tinggi :",0)
  hitung = st.button("Hitung Luas")

  if hitung:
    luas = 1/2 * alas * tinggi
    st.write("Luas Segitiga adalah : ", luas)
    # st.success(f"Luas Segitiga adalah : {luas}")  

# Halaman Hitung Luas Lingkaran
if (selected == "Luas Lingkaran"):
  st.title("Hitung Luas Lingkaran")

  st.image("data:image/png;base64,")

  jari = st.number_input("Masukkan Jari-jari :",0)
  hitung = st.button("Hitung Luas")

  if hitung:
    luas = 22/7 * jari * jari
    st.write("Luas Lingkaran adalah : ", luas)
    # st.success(f"Luas Lingkaran adalah : {luas}")

# Halaman Hitung Luas Jajar Genjang
if (selected == "Luas Jajar Genjang"):
  st.title("Hitung Luas Jajar Genjang")

  st.image("data:image/jpeg;base64,")

  alas = st.number_input("Masukkan Alas :",0)
  tinggi = st.number_input("Masukkan Tinggi :",0)
  hitung = st.button("Hitung Luas")

  if hitung:
    luas = alas * tinggi
    st.write("Luas Jajar Genjang adalah : ", luas)
    # st.success(f"Luas Jajar Genjang adalah : {luas}")

# Halaman Hitung Luas Trapesium
if (selected == "Luas Trapesium"):
  st.title("Hitung Luas Trapesium")

  st.image("data:image/png;base64,")

  sisi_a = st.number_input("Masukkan sisi_a :",0)
  sisi_b = st.number_input("Masukkan sisi_b :",0)
  tinggi = st.number_input("Masukkan Tinggi :",0)

  hitung = st.button("Hitung Luas")

  if hitung:
    luas = 1/2 * (sisi_a + sisi_b) * tinggi
    st.write("Luas Trapesium adalah : ", luas)
    # st.success(f"Luas Trapesium adalah : {luas}") 